public class ${Name}Exception extends Exception {

    public ${Name}Exception() {
        super();
    }

    public ${Name}Exception(String message) {
        super(message);
    }

    public ${Name}Exception(Throwable cause) {
        super(cause);
    }

    public ${Name}Exception(String message, Throwable cause) {
        super(message, cause);
    }
}
